package leetcode

import (
	"fmt"
	"sort"
)

// https://leetcode.com/problems/3sum-closest/
func (s *LtCodeSuite) TestP16() {
	cases := []struct {
		nums   []int
		target int
		wanted int
	}{
		{[]int{-1, 2, 1, -4}, 1, 2},
		{[]int{0, 0, 0}, 1, 0},
	}
	s.Run("3 sum closest", func() {
		for _, e := range cases {
			s.Equal(e.wanted, threeSumClosest(e.nums, e.target))
		}
	})
}

func threeSumClosest(nums []int, target int) int {
	sort.Ints(nums)
	output := nums[0] + nums[1] + nums[2]
	l := len(nums)

	for i, a := range nums {
		start, end := i+1, l-1
		for start < end {
			sum := a + nums[start] + nums[end]
			if abs(sum-target) < abs(output-target) {
				output = sum
			}
			if sum > target {
				end--
			} else if sum < target {
				start++
			} else {
				return sum // equal target!
			}
		}
	}
	return output
}

func abs(a int) int {
	if a > 0 {
		return a
	}
	return -a
}

