func threeSum(nums []int) [][]int {
	var result [][]int

	n := len(nums)
	if n < 3 {
		return result
	}

	sort.Ints(nums)

	for i := 0; i < n-2; i++ {
		a := nums[i]
		if i != 0 && nums[i-1] == a { // 避免首元素重复
			continue
		}

		start, end := i+1, n-1
		for start < end {
			b := nums[start]
			c := nums[end]
			sum := a + b + c
			if sum == 0 {
				result = append(result, []int{a, b, c})
				e := start
				for e < end && nums[e] == b { // 避免次元素重复
					e++
				}
				start = e
			} else if sum > 0 {
				end--
			} else {
				start++
			}
		}
	}
	return result
}

func threeSumJustWorkWithMap(nums []int) [][]int {
	var result [][]int
	n := len(nums)
	if n < 3 {
		return result
	}

	// 首先保证有序
	sort.Ints(nums)

	m := make(map[string]interface{})
	//cnt := 0
	for i := 0; i < n-2; i++ {
		a := nums[i]
		j := i + 1
		k := n - 1
		for j < k {
			//cnt++
			b, c := nums[j], nums[k]
			//fmt.Printf("[%d]==> [%d]=%d [%d]=%d [%d]=%d with n=%d\n",
			//	cnt, i, a, j, b, k, c, n)

			sum := a + b + c
			if sum == 0 {
				// uniq check
				key := fmt.Sprintf("%d-%d-%d", a, b, c)
				if _, ok := m[key]; ok {
					j++
					k--
					continue
				}
				m[key] = true

				result = append(result, []int{a, b, c})
				//fmt.Printf("[%d]==> [%d]=%d [%d]=%d [%d]=%d with n=%d\n",
				//	cnt, i, a, j, b, k, c, n)
				continue
			}
			if sum > 0 {
				k--
			} else {
				j++
			}
		}
	}
	return result
}

// https://leetcode.com/problems/two-sum/
func (s *LtCodeSuite) TestP1() {
	cases := []struct {
		nums   []int
		target int
		wanted []int
	}{
		{[]int{2, 7, 11, 15}, 9, []int{0, 1}},
		{[]int{3, 2, 4}, 6, []int{1, 2}},
		{[]int{3, 3}, 6, []int{0, 1}},
		{[]int{6, 3, 2, 1}, 8, []int{0, 2}},
	}
	s.Run("BruteForce", func() {
		for _, e := range cases {
			s.Equal(e.wanted, twoSumByBruteForce(e.nums, e.target))
		}
	})

	s.Run("TwoPoints after sort", func() {
		for _, e := range cases {
			cn := make([]int, len(e.nums))
			copy(cn, e.nums)
			sort.Reverse(sort.IntSlice(cn))
			// NOTE: only works on sorted data
			if sort.IntsAreSorted(e.nums) || sort.IntsAreSorted(cn) {
				fmt.Printf("test on case: %v target: %d\n", e.nums, e.target)
				s.Equal(e.wanted, twoSumBySortThenTwoPoints(e.nums, e.target))
			}
		}
	})

	s.Run("OnePassMap without order", func() {
		for _, e := range cases {
			result := twoSumByOnePassMap(e.nums, e.target)
			sort.Slice(result, func(i, j int) bool {
				return result[i] <= result[j]
			})
			s.Equal(e.wanted, result)
		}
	})
}

func twoSumByBruteForce(nums []int, target int) []int {
	for i, a := range nums {
		for j := i + 1; j < len(nums); j++ {
			if a+nums[j] == target {
				return []int{i, j}
			}
		}
	}
	return nil
}

func twoSumByOnePassMap(nums []int, target int) []int {
	m := make(map[int]int)
	for i, a := range nums {
		diff := target - a
		if j, ok := m[diff]; ok {
			return []int{i, j}
		}
		m[a] = i
	}
	return nil
}

func twoSumBySortThenTwoPoints(nums []int, target int) []int {
	sort.Ints(nums) // should ensure sorted firstly

	i, j := 0, len(nums)-1
	for i < j {
		sum := nums[i] + nums[j]
		if sum == target {
			return []int{i, j}
		}
		if sum < target {
			i++
		}
		if sum > target {
			j--
		}
	}
	return nil
}
